// SPDX-License-Identifier: MIT

// Copy of https://github.com/witnet/elliptic-curve-solidity/blob/master/examples/Secp256k1.sol

pragma solidity ^0.8.0;

import "./EllipticCurve.sol";

/**
 ** @title Secp256k1 Elliptic Curve
 ** @notice Example of particularization of Elliptic Curve for secp256k1 curve
 ** @author Witnet Foundation
 */
library Secp256k1 {
    uint256 internal constant GX =
        0x79BE667EF9DCBBAC55A06295CE870B07029BFCDB2DCE28D959F2815B16F81798;
    uint256 internal constant GY =
        0x483ADA7726A3C4655DA4FBFC0E1108A8FD17B448A68554199C47D08FFB10D4B8;
    uint256 internal constant AA = 0;
    uint256 internal constant BB = 7;
    uint256 internal constant PP =
        0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFEFFFFFC2F;
    uint256 internal constant NN =
        0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFEBAAEDCE6AF48A03BBFD25E8CD0364141;

    /// @notice Public Key derivation from private key
    /// Warning: this is just an example. Do not expose your private key.
    /// @param privKey The private key
    /// @return (qx, qy) The Public Key
    function derivePubKey(
        uint256 privKey
    ) external pure returns (uint256, uint256) {
        return EllipticCurve.ecMul(privKey, GX, GY, AA, PP);
    }
}

contracts/NostrSignatures.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "./BIP340/Bip340Ecrec.sol";
import "./utils/HexStrings.sol";

/**
 * @title NostrSignatures
 * @notice Library for verifying Nostr protocol signatures using BIP340 Schnorr signatures
 * @dev This library provides functionality to verify Nostr signatures by constructing
 *      the appropriate message format and using BIP340 signature verification.
 *      Nostr signatures follow a specific JSON-like format that needs to be constructed
 *      and hashed before verification.
 * @author Senior Smart Contract Engineer
 */
library NostrSignatures {
    // ============ Constants ============

    /// @notice Expected signature length for BIP340 signatures (64 bytes: 32 bytes R + 32 bytes s)
    uint256 private constant SIGNATURE_LENGTH = 64;

    /// @notice Nostr event kind for authentication (96024 is a common kind for auth events)
    uint256 private constant NOSTR_AUTH_KIND = 96024;

    /// @notice Nostr event created_at timestamp (0 for immediate verification)
    uint256 private constant NOSTR_CREATED_AT = 0;

    /// @notice Nostr event tags array (empty for auth events)
    string private constant NOSTR_TAGS = "[]";

    // ============ Errors ============

    /// @notice Thrown when signature length is invalid
    error InvalidSignatureLength();

    /// @notice Thrown when signature verification fails
    error SignatureVerificationFailed();

    /// @notice Thrown when owner address is invalid (zero address)
    error InvalidOwnerAddress();

    // ============ Events ============

    /// @notice Emitted when a Nostr signature is successfully verified
    /// @param owner The Nostr public key that signed the message
    /// @param userOpHash The hash of the user operation that was signed
    event NostrSignatureVerified(uint256 indexed owner, bytes32 indexed userOpHash);

    // ============ Public Functions ============

    /**
     * @notice Verifies a Nostr signature against a user operation hash
     * @dev Constructs the Nostr event JSON format, hashes it, and verifies the BIP340 signature
     *      The Nostr event format is: [0, "<pubkey>", 0, 96024, [], "<userOpHash>"]
     * @param owner The Nostr public key (x-coordinate) that should have signed the message
     * @param signature The 64-byte BIP340 signature (32 bytes R + 32 bytes s)
     * @param userOpHash The hash of the user operation being verified
     * @return success True if the signature is valid, false otherwise
     * @custom:security This function validates input parameters and uses secure BIP340 verification
     */
    function verifyNostrSignature(
        uint256 owner,
        bytes memory signature,
        bytes32 userOpHash
    ) internal returns (bool success) {
        // Validate input parameters
        if (signature.length != SIGNATURE_LENGTH) {
            revert InvalidSignatureLength();
        }

        if (owner == 0) {
            revert InvalidOwnerAddress();
        }

        // Construct the Nostr event message in the required JSON-like format
        // Format: [0, "<pubkey>", 0, 96024, [], "<userOpHash>"]
        bytes memory nostrEventMessage = abi.encodePacked(
            '[',
            _uintToString(NOSTR_CREATED_AT),
            ',"',
            HexStrings.toHexString(owner),
            '",',
            _uintToString(NOSTR_CREATED_AT),
            ',',
            _uintToString(NOSTR_AUTH_KIND),
            ',',
            NOSTR_TAGS,
            ',"',
            HexStrings.toHexString(uint256(userOpHash)),
            '"]'
        );

        // Hash the constructed message using SHA256
        bytes32 messageHash = sha256(nostrEventMessage);

        // Extract signature components using assembly for gas efficiency
        uint256 signatureR;
        uint256 signatureS;
        assembly ("memory-safe") {
            // Load R (first 32 bytes of signature)
            // signature.length is already validated to be 64 bytes
            signatureR := mload(add(signature, 0x20))
            // Load s (second 32 bytes of signature)
            signatureS := mload(add(signature, 0x40))
        }

        // Verify the BIP340 signature
        success = BIP340Ecrec.verify(owner, signatureR, signatureS, messageHash);

        // Revert with custom error if verification fails
        if (!success) {
            revert SignatureVerificationFailed();
        }

        // Emit event for successful verification
        emit NostrSignatureVerified(owner, userOpHash);
    }

    // ============ Private Helper Functions ============

    /**
     * @notice Converts a uint256 to its string representation
     * @dev Internal helper function for constructing Nostr event messages
     * @param value The uint256 value to convert
     * @return The string representation of the value
     */
    function _uintToString(uint256 value) private pure returns (string memory) {
        if (value == 0) {
            return "0";
        }

        uint256 temp = value;
        uint256 digits;

        // Count digits
        while (temp != 0) {
            digits++;
            temp /= 10;
        }

        // Allocate memory for the string
        bytes memory buffer = new bytes(digits);

        // Convert to string (right to left)
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }

        return string(buffer);
    }

    /**
     * @notice Verifies a Nostr signature against a user operation hash (non-reverting version)
     * @dev Same as verifyNostrSignature but returns false instead of reverting on failure
     * @param owner The Nostr public key (x-coordinate) that should have signed the message
     * @param signature The 64-byte BIP340 signature (32 bytes R + 32 bytes s)
     * @param userOpHash The hash of the user operation being verified
     * @return success True if the signature is valid, false otherwise
     */
    function tryVerifyNostrSignature(
        uint256 owner,
        bytes memory signature,
        bytes32 userOpHash
    ) internal returns (bool success) {
        // Validate input parameters
        if (signature.length != SIGNATURE_LENGTH || owner == 0) {
            return false;
        }

        // Construct the Nostr event message in the required JSON-like format
        bytes memory nostrEventMessage = abi.encodePacked(
            '[',
            _uintToString(NOSTR_CREATED_AT),
            ',"',
            HexStrings.toHexString(owner),
            '",',
            _uintToString(NOSTR_CREATED_AT),
            ',',
            _uintToString(NOSTR_AUTH_KIND),
            ',',
            NOSTR_TAGS,
            ',"',
            HexStrings.toHexString(uint256(userOpHash)),
            '"]'
        );

        // Hash the constructed message using SHA256
        bytes32 messageHash = sha256(nostrEventMessage);

        // Extract signature components using assembly for gas efficiency
        uint256 signatureR;
        uint256 signatureS;
        assembly ("memory-safe") {
            signatureR := mload(add(signature, 0x20))
            signatureS := mload(add(signature, 0x40))
        }

        // Verify the BIP340 signature
        success = BIP340Ecrec.verify(owner, signatureR, signatureS, messageHash);

        // Emit event for successful verification
        if (success) {
            emit NostrSignatureVerified(owner, userOpHash);
        }
    }
}

contracts/NpubAccount.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@account-abstraction/contracts/core/BaseAccount.sol";
import "@account-abstraction/contracts/core/Helpers.sol";
import "@account-abstraction/contracts/samples/callback/TokenCallbackHandler.sol";

import "./NostrSignatures.sol";

/**
 * @title NpubAccount
 * @notice A minimal smart account implementation that uses Nostr signatures for authentication
 * @dev This contract implements ERC-4337 account abstraction with Nostr protocol integration.
 *      It allows users to control their account using Nostr private keys instead of Ethereum private keys.
 *      The account supports execution of arbitrary transactions, ETH handling, and deposit management.
 * @author Senior Smart Contract Engineer
 */
contract NpubAccount is BaseAccount, TokenCallbackHandler, UUPSUpgradeable, Initializable, ReentrancyGuard {
    // ============ State Variables ============

    /// @notice The Nostr public key (x-coordinate) that owns this account
    uint256 public owner;

    /// @notice The EntryPoint contract for account abstraction operations
    IEntryPoint private immutable _entryPoint;

    // ============ Events ============

    /// @notice Emitted when the account is initialized with a new owner
    /// @param entryPoint The EntryPoint contract address
    /// @param owner The Nostr public key that owns this account
    event AccountInitialized(IEntryPoint indexed entryPoint, uint256 indexed owner);

    /// @notice Emitted when ETH is deposited to the EntryPoint
    /// @param amount The amount of ETH deposited
    /// @param newBalance The new deposit balance in EntryPoint
    event DepositAdded(uint256 indexed amount, uint256 indexed newBalance);

    /// @notice Emitted when ETH is withdrawn from the EntryPoint
    /// @param to The address that received the withdrawn funds
    /// @param amount The amount of ETH withdrawn
    /// @param newBalance The new deposit balance in EntryPoint
    event DepositWithdrawn(address indexed to, uint256 indexed amount, uint256 indexed newBalance);

    /// @notice Emitted when the account implementation is upgraded
    /// @param newImplementation The address of the new implementation
    event AccountUpgraded(address indexed newImplementation);

    // ============ Errors ============

    /// @notice Thrown when a function is called by an unauthorized address
    error UnauthorizedCaller();

    /// @notice Thrown when the owner address is invalid (zero)
    error InvalidOwner();

    /// @notice Thrown when attempting to withdraw more than available deposit
    error InsufficientDeposit();

    /// @notice Thrown when attempting to withdraw to zero address
    error InvalidWithdrawAddress();

    /// @notice Thrown when attempting to deposit zero amount
    error ZeroDepositAmount();

    /// @notice Thrown when EntryPoint address is invalid
    error InvalidEntryPoint();

    // ============ Modifiers ============

    /// @notice Restricts function access to the account owner only
    modifier onlyOwner() {
        _onlyOwner();
        _;
    }

    // ============ View Functions ============

    /// @notice Returns the EntryPoint contract address
    /// @return The EntryPoint contract instance
    function entryPoint() public view virtual override returns (IEntryPoint) {
        return _entryPoint;
    }

    // ============ Receive Function ============

    /// @notice Allows the contract to receive ETH
    /// @dev This function enables the contract to accept ETH transfers
    receive() external payable {
        // Intentionally empty - allows contract to receive ETH
    }

    // ============ Constructor ============

    /// @notice Initializes the contract with the EntryPoint address
    /// @dev Disables initializers to prevent direct initialization
    /// @param anEntryPoint The EntryPoint contract address for account abstraction
    constructor(IEntryPoint anEntryPoint) {
        if (address(anEntryPoint) == address(0)) {
            revert InvalidEntryPoint();
        }
        _entryPoint = anEntryPoint;
        _disableInitializers();
    }

    // ============ Internal Functions ============

    /// @notice Internal function to check if the caller is the owner
    /// @dev The account itself (which gets redirected through execute()) is considered the owner
    function _onlyOwner() internal view {
        if (msg.sender != address(this)) {
            revert UnauthorizedCaller();
        }
    }

    // ============ Initialization Functions ============

    /// @notice Initializes the account with a Nostr public key owner
    /// @dev The EntryPoint member is immutable to reduce gas consumption. To upgrade EntryPoint,
    ///      a new implementation must be deployed with the new EntryPoint address, then upgrading
    ///      the implementation by calling `upgradeTo()`
    /// @param anOwner The Nostr public key (x-coordinate) that will own this account
    function initialize(uint256 anOwner) public virtual initializer {
        _initialize(anOwner);
    }

    /// @notice Internal initialization function
    /// @dev Sets the owner and emits initialization event
    /// @param anOwner The Nostr public key (x-coordinate) that will own this account
    function _initialize(uint256 anOwner) internal virtual {
        if (anOwner == 0) {
            revert InvalidOwner();
        }
        owner = anOwner;
        emit AccountInitialized(_entryPoint, owner);
    }

    // ============ Execution Functions ============

    /// @notice Validates that the function call went through EntryPoint
    /// @dev Override from BaseAccount to ensure proper execution context
    function _requireFromEntryPointOrSelf() internal view virtual {
        if (msg.sender != address(entryPoint()) && msg.sender != address(this)) {
            revert UnauthorizedCaller();
        }
    }

    /// @notice Validates a user operation signature using Nostr protocol
    /// @dev Implements the template method from BaseAccount for signature validation
    /// @param userOp The packed user operation containing the signature
    /// @param userOpHash The hash of the user operation to validate against
    /// @return validationData The validation result (SIG_VALIDATION_SUCCESS or SIG_VALIDATION_FAILED)
    function _validateSignature(PackedUserOperation calldata userOp, bytes32 userOpHash)
        internal 
        override 
        virtual 
        returns (uint256 validationData) 
    {
        // UserOpHash can be generated using eth_signTypedData_v4
        if (NostrSignatures.verifyNostrSignature(owner, userOp.signature, userOpHash)) {
            return SIG_VALIDATION_SUCCESS;
        }
        return SIG_VALIDATION_FAILED;
    }

    // ============ Deposit Management Functions ============

    /// @notice Returns the current account deposit balance in the EntryPoint
    /// @return The current deposit balance in wei
    function getDeposit() public view returns (uint256) {
        return entryPoint().balanceOf(address(this));
    }

    /// @notice Deposits ETH to the EntryPoint for this account
    /// @dev The deposited ETH is used to pay for gas fees during user operations
    function addDeposit() public payable nonReentrant {
        if (msg.value == 0) {
            revert ZeroDepositAmount();
        }

        // Checks-Effects-Interactions pattern
        uint256 amount = msg.value;
        entryPoint().depositTo{value: amount}(address(this));
        uint256 newBalance = entryPoint().balanceOf(address(this));

        emit DepositAdded(amount, newBalance);
    }

    /// @notice Withdraws ETH from the account's EntryPoint deposit
    /// @dev Only the account owner can withdraw funds
    /// @param withdrawAddress The address to send the withdrawn funds to
    /// @param amount The amount of ETH to withdraw in wei
    function withdrawDepositTo(address payable withdrawAddress, uint256 amount) public onlyOwner nonReentrant {
        if (withdrawAddress == address(0)) {
            revert InvalidWithdrawAddress();
        }

        // Checks-Effects-Interactions pattern
        uint256 currentDeposit = entryPoint().balanceOf(address(this));
        if (amount > currentDeposit) {
            revert InsufficientDeposit();
        }

        entryPoint().withdrawTo(withdrawAddress, amount);
        uint256 newBalance = entryPoint().balanceOf(address(this));

        emit DepositWithdrawn(withdrawAddress, amount, newBalance);
    }

    // ============ Upgrade Functions ============

    /// @notice Authorizes the upgrade to a new implementation
    /// @dev Only the account owner can authorize upgrades
    /// @param newImplementation The address of the new implementation contract
    function _authorizeUpgrade(address newImplementation) internal view override {
        if (newImplementation == address(0)) {
            revert InvalidEntryPoint();
        }
        _onlyOwner();
    }
}

contracts/NpubAccountFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/utils/Create2.sol";
import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";

import "@account-abstraction/contracts/interfaces/IEntryPoint.sol";
import "./NpubAccount.sol";

/**
 * @title INpubAccountFactory
 * @notice Interface for the NpubAccountFactory contract
 * @dev Defines the interface for creating and managing NpubAccount instances
 */
interface INpubAccountFactory {
    /// @notice Creates a new NpubAccount or returns existing one
    /// @param owner The Nostr public key (x-coordinate) that will own the account
    /// @param salt The salt used for deterministic address generation
    /// @return ret The created or existing NpubAccount instance
    function createAccount(uint256 owner, uint256 salt) external returns (NpubAccount ret);

    /// @notice Calculates the counterfactual address of an account
    /// @param owner The Nostr public key (x-coordinate) that will own the account
    /// @param salt The salt used for deterministic address generation
    /// @return The computed address of the account
    function getAddress(uint256 owner, uint256 salt) external view returns (address);

    /// @notice Returns the account implementation address
    /// @return The address of the NpubAccount implementation
    function accountImplementation() external view returns (NpubAccount);
}

/**
 * @title NpubAccountFactory
 * @notice Factory contract for creating NpubAccount instances using deterministic addresses
 * @dev A UserOperations "initCode" holds the address of the factory, and a method call (to createAccount).
 *      The factory's createAccount returns the target account address even if it is already installed.
 *      This way, the entryPoint.getSenderAddress() can be called either before or after the account is created.
 * @author Senior Smart Contract Engineer
 */
contract NpubAccountFactory is INpubAccountFactory {
    // ============ State Variables ============

    /// @notice The immutable account implementation contract
    NpubAccount public immutable override accountImplementation;

    // ============ Custom Errors ============

    /// @notice Thrown when caller is not the authorized sender creator
    error UnauthorizedCaller();

    /// @notice Thrown when account creation fails
    error AccountCreationFailed();

    // ============ Events ============

    /// @notice Emitted when a new account is created
    /// @param account The address of the created account
    /// @param owner The Nostr public key that owns the account
    /// @param salt The salt used for address generation
    /// @param isNew Whether this is a newly created account (true) or existing (false)
    event AccountCreated(address indexed account, uint256 indexed owner, uint256 indexed salt, bool isNew);

    // ============ Constructor ============

    /// @notice Initializes the factory with the EntryPoint contract
    /// @param _entryPoint The EntryPoint contract address
    constructor(IEntryPoint _entryPoint) {
        accountImplementation = new NpubAccount(_entryPoint);
    }

    // ============ External Functions ============

    /// @notice Creates a new NpubAccount or returns existing one
    /// @dev Returns the address even if the account is already deployed.
    ///      Note that during UserOperation execution, this method is called only if the account is not deployed.
    ///      This method returns an existing account address so that entryPoint.getSenderAddress() would work even after account creation
    /// @param owner The Nostr public key (x-coordinate) that will own the account
    /// @param salt The salt used for deterministic address generation
    /// @return ret The created or existing NpubAccount instance
    function createAccount(uint256 owner, uint256 salt) external override returns (NpubAccount ret) {
        address addr = this.getAddress(owner, salt);
        uint256 codeSize = addr.code.length;

        // Return existing account if already deployed
        if (codeSize > 0) {
            emit AccountCreated(addr, owner, salt, false);
            return NpubAccount(payable(addr));
        }

        // Create new account using CREATE2 for deterministic address
        try new ERC1967Proxy{salt: bytes32(salt)}(
            address(accountImplementation),
            abi.encodeCall(NpubAccount.initialize, (owner))
        ) returns (ERC1967Proxy proxy) {
            ret = NpubAccount(payable(address(proxy)));
            emit AccountCreated(address(ret), owner, salt, true);
        } catch {
            revert AccountCreationFailed();
        }
    }

    /// @notice Calculates the counterfactual address of an account as it would be returned by createAccount()
    /// @dev Uses CREATE2 to compute the deterministic address based on salt and initialization data
    /// @param owner The Nostr public key (x-coordinate) that will own the account
    /// @param salt The salt used for deterministic address generation
    /// @return The computed address of the account
    function getAddress(uint256 owner, uint256 salt) external view override returns (address) {
        return Create2.computeAddress(
            bytes32(salt), 
            keccak256(abi.encodePacked(
                type(ERC1967Proxy).creationCode,
                abi.encode(
                    address(accountImplementation),
                    abi.encodeCall(NpubAccount.initialize, (owner))
                )
            ))
        );
    }
}

